import React from 'react';
import { MessageType } from './ChatInterface';

interface MessageProps {
  type: MessageType;
  content: string;
  timestamp: number;
  sender: string;
}

/**
 * Message component for displaying chat messages with appropriate styling based on type
 */
const Message: React.FC<MessageProps> = ({ type, content, timestamp, sender }) => {
  return (
    <div key={id} className={`message ${type}-message fade-in`}>
      <div className="message-header">
        <span className="sender">{sender}</span>
        <span className="timestamp">
          {new Date(timestamp).toLocaleTimeString()}
        </span>
      </div>
      <div className="message-content">{content}</div>
    </div>
  );
};

export default Message;
