import React, { useState } from 'react';
import { Outlet } from 'react-router-dom';
import { useTheme } from '../contexts/ThemeContext';
import { useApi } from '../contexts/ApiContext';
import { useGameState } from '../contexts/GameStateContext';
import Header from '../components/Header';
import Footer from '../components/Footer';
import GameStatePanel from '../components/GameStatePanel';
import CharacterPanel from '../components/CharacterPanel';

const MainLayout: React.FC = () => {
  const { theme } = useTheme();
  const { isConnected } = useApi();
  const { gameState } = useGameState();

  const [leftPanelOpen, setLeftPanelOpen] = useState(false);
  const [rightPanelOpen, setRightPanelOpen] = useState(false);

  const toggleLeftPanel = () => {
    setLeftPanelOpen(!leftPanelOpen);
    if (!leftPanelOpen) setRightPanelOpen(false);
  };

  const toggleRightPanel = () => {
    setRightPanelOpen(!rightPanelOpen);
    if (!rightPanelOpen) setLeftPanelOpen(false);
  };

  return (
    <div className={`app theme-${theme}`}>
      <Header 
        toggleLeftPanel={toggleLeftPanel}
        toggleRightPanel={toggleRightPanel}
      />

      <div className="main-content">
        {/* Game State Panel (Left) */}
        <div className={`left-panel ${leftPanelOpen ? 'open' : ''}`}>
          <div className="panel-header">
            <h2>Game State</h2>
          </div>
          <div className="panel-content">
            <div className="game-status">
              <div className={`status-badge gradient-${gameState.status}`}>
                {gameState.status.charAt(0).toUpperCase() + gameState.status.slice(1)}
              </div>
              <h3>{gameState.sessionName}</h3>
              <p>System: {gameState.gameSystem}</p>
            </div>

            <div className="game-events">
              <h4>Recent Events</h4>
              <ul className="event-list">
                {gameState.events.slice(-5).reverse().map(event => (
                  <li key={event.id} className="event-item">
                    <div className="event-time">
                      {new Date(event.timestamp).toLocaleTimeString()}
                    </div>
                    <div className="event-description">
                      {event.description}
                    </div>
                  </li>
                ))}
              </ul>
            </div>
          </div>
        </div>

        {/* Main Chat Panel (Center) */}
        <div className="center-panel">
          <Outlet />
        </div>

        {/* Character/Game Info Panel (Right) */}
        <div className={`right-panel ${rightPanelOpen ? 'open' : ''}`}>
          <div className="panel-header">
            <h2>Characters & Info</h2>
          </div>
          <div className="panel-content">
            <div className="character-list">
              <h4>Characters</h4>
              {gameState.characters.length === 0 ? (
                <p className="empty-state">No characters created yet.</p>
              ) : (
                <ul>
                  {gameState.characters.map(character => (
                    <li 
                      key={character.id}
                      className={`character-item ${character.id === gameState.activeCharacterId ? 'active' : ''}`}
                    >
                      <div className="character-name">{character.name}</div>
                      <div className="character-type">{character.type}</div>
                    </li>
                  ))}
                </ul>
              )}
            </div>

            <div className="location-info">
              <h4>Current Location</h4>
              <p>{gameState.currentLocation || 'Unknown'}</p>
            </div>
          </div>
        </div>

        {/* Mobile overlay for panels */}
        <div 
          className={`panel-overlay ${leftPanelOpen || rightPanelOpen ? 'active' : ''}`}
          onClick={() => {
            setLeftPanelOpen(false);
            setRightPanelOpen(false);
          }}
        ></div>
      </div>

      <Footer />
    </div>
  );
};

export default MainLayout;
